import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as h,e as r}from"./app.67d17080.js";const d={},i=r('<h2 id="_1-\u5E8F\u8A00" tabindex="-1"><a class="header-anchor" href="#_1-\u5E8F\u8A00" aria-hidden="true">#</a> 1. \u5E8F\u8A00</h2><p>\u8FD9\u4E00\u90E8\u5206\u5185\u5BB9\u5199\u7ED9\u65F6\u95F4\u7CBE\u529B\u5145\u8DB3\uFF0C\u5E0C\u671B\u80FD\u591F\u66F4\u597D\u5730\u5E2E\u52A9\u5230\u8DE8\u6027\u522B\u793E\u7FA4\u7684\u8D21\u732E\u8005\u4EEC\u3002\u5B83\u8BE6\u5C3D\u5730\u9610\u8FF0\u4E86 RLE.wiki \u5927\u5B66\u6307\u5357\u90E8\u5206\u7684\u5185\u5BB9\u89C4\u8303\u3002\u5982\u679C\u4F60\u7684\u6295\u7A3F\u7B26\u5408\u89C4\u8303\uFF0C\u53EF\u4EE5\u66F4\u5FEB\u5730\u63D0\u4EA4\u81F3 Wiki \u4E0A\uFF0C\u4E5F\u53EF\u4EE5\u51CF\u8F7B\u5BA1\u7A3F\u4EBA\u7684\u5DE5\u4F5C\u8D1F\u62C5\u3002</p><h2 id="_2-\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#_2-\u539F\u5219" aria-hidden="true">#</a> 2. \u539F\u5219</h2><p>RLE.wiki \u7684\u4E00\u4E2A\u6821\u56ED\u9875\u9762\u4E00\u822C\u5BF9\u5E94\u4E00\u4E2A\u9AD8\u8003\u62DB\u751F\u4EE3\u7801\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E00\u4E2A\u5B66\u6821\u5982\u679C\u6309\u4E13\u4E1A\u6216\u5E74\u7EA7\u88AB\u5206\u914D\u4E3A\u4E0D\u540C\u7684\u6821\u533A\uFF0C\u5B66\u751F\u53EF\u80FD\u4F1A\u5F80\u6765\u4E8E\u4E0D\u540C\u6821\u533A\u6765\u4E0A\u8BFE\u6216\u529E\u624B\u7EED\uFF0C\u90A3\u4E48\u5C06\u5B83\u4EEC\u653E\u5728\u540C\u4E00\u4E2A\u9875\u9762\u4E0B\uFF1B\u5982\u679C\u9AD8\u8003\u62DB\u751F\u65F6\u5C31\u5DF2\u7ECF\u5206\u8BBE\u4E0D\u540C\u7684\u4EE3\u7801\uFF08\u5982\u4E1C\u5317\u5927\u5B66\u79E6\u7687\u5C9B\u5206\u6821\uFF09\uFF0C\u5F7C\u6B64\u9AD8\u5EA6\u72EC\u7ACB\u65E0\u9700\u5F80\u6765\uFF0C\u90A3\u4E48\u5C31\u5355\u72EC\u8BBE\u7F6E\u9875\u9762\u3002\u5982\u6709\u7279\u6B8A\u60C5\u51B5\u53EF\u4EE5\u7075\u6D3B\u5B89\u6392\u3002</p><p>\u6295\u7A3F\u539F\u5219\u4E0A\u5E94\u8BE5\u4E3A\u8DE8\u6027\u522B\u5173\u5FC3\u800C\u4ECE\u5176\u4ED6\u6E20\u9053\u96BE\u4EE5\u83B7\u53D6\u7684\u5185\u5BB9\u3002\u4F8B\u5982\uFF0C\u5927\u90E8\u5206\u4EBA\u4E0D\u4F1A\u5173\u5FC3\u5B66\u6821\u662F\u5426\u6709\u7B2C\u4E09\u536B\u751F\u95F4\u3001\u6821\u533B\u9662\u80FD\u5426\u5F00\u5177\u6FC0\u7D20\u68C0\u67E5\u7B49\u95EE\u9898\uFF0C\u800C\u8FD9\u5BF9\u4E8E\u8DE8\u6027\u522B\u800C\u8A00\u5C31\u8F83\u4E3A\u91CD\u8981\u3002\u81F3\u4E8E\u5B66\u6821\u6392\u540D\u3001\u5B66\u751F\u5C31\u4E1A\u60C5\u51B5\u3001\u8001\u5E08\u6388\u8BFE\u6C34\u5E73\u3001\u98DF\u5802\u8D28\u91CF\u8FD9\u4E9B\u95EE\u9898\uFF0C\u662F\u7EDD\u5927\u90E8\u5206\u5B66\u751F\u90FD\u4F1A\u5173\u5FC3\u7684\uFF0C\u4E5F\u5F88\u5BB9\u6613\u67E5\u5230\uFF0C\u5C31\u4E0D\u5FC5\u653E\u5728 Wiki \u4E0A\u3002</p><p>\u6295\u7A3F\u7684\u6B63\u6587\u90E8\u5206\u5E94\u4E3A\u957F\u671F\u6709\u6548\u7684\u3001\u5BA2\u89C2\u7684\u5185\u5BB9\uFF0C\u4F8B\u5982\u5B66\u6821\u7684\u5730\u7406\u4F4D\u7F6E\uFF0C\u5BDD\u5BA4\u662F\u5426\u6709\u72EC\u7ACB\u536B\u6D74\u7B49\uFF1B\u800C\u5BF9\u4E8E\u5B66\u6821\u7684\u4E3B\u89C2\u8BC4\u8BBA\u7B49\u5E94\u8BE5\u4F7F\u7528\u5F15\u7528\u7684\u683C\u5F0F\u3002</p><h2 id="_3-\u683C\u5F0F" tabindex="-1"><a class="header-anchor" href="#_3-\u683C\u5F0F" aria-hidden="true">#</a> 3. \u683C\u5F0F</h2><p>\u6821\u56ED\u90E8\u5206\u6BCF\u4E2A\u9875\u9762\u5206\u4E3A\u6982\u51B5\u3001\u5730\u7406\u4F4D\u7F6E\u53CA\u4EA4\u901A\u3001\u519B\u8BAD\u4E0E\u4F53\u80B2\u3001\u5BBF\u820D\u60C5\u51B5\u4E0E\u57FA\u7840\u8BBE\u65BD\u3001\u533B\u7597\u8D44\u6E90\u3001LGBT \u53CB\u5584\u6897\u6982\u3001\u9662\u7CFB\u63A2\u8DEF\u3001\u5176\u4ED6\u4FE1\u606F\u51E0\u4E2A\u90E8\u5206\u3002\u6587\u4EF6\u540D\u4E3A\u5B66\u6821\u82F1\u6587\u7F29\u5199.md\u3002</p><h3 id="_3-1-\u6982\u51B5" tabindex="-1"><a class="header-anchor" href="#_3-1-\u6982\u51B5" aria-hidden="true">#</a> 3.1 \u6982\u51B5</h3><p>\u6982\u51B5\u90E8\u5206\u5E94\u5305\u542B\u5B66\u6821\u7684\u4E2D\u6587\u5168\u79F0\u3001\u82F1\u6587\u5168\u7A0B\u3001\u82F1\u6587\u7F29\u5199\u3001\u5B98\u7F51\u5730\u5740\uFF0C\u4F7F\u7528 <code>[\u4E2D\u6587\u5168\u79F0](\u5B98\u7F51\u5730\u5740)\uFF08\u82F1\u6587\u5168\u79F0\uFF0C\u82F1\u6587\u7F29\u5199\uFF09</code> \u7684\u683C\u5F0F\u3002\u63A5\u4E0B\u6765\u53EF\u4EE5\u7B80\u5355\u9648\u8FF0\u5B66\u6821\u7684\u6574\u4F53\u60C5\u51B5\uFF0C\u4F8B\u5982\u8BBE\u7F6E\u6709\u54EA\u4E9B\u5B66\u79D1\uFF0C\u662F\u5426\u4E3A\u53CC\u4E00\u6D41\u5B66\u6821\u7B49\u7B49\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u5982\u679C\u6709\u5BF9\u5B66\u6821\u7684\u6574\u4F53\u8BC4\u4EF7\uFF0C\u4E5F\u53EF\u4EE5\u4EE5\u5F15\u7528\u7684\u683C\u5F0F\u5199\u5728\u8FD9\u4E00\u90E8\u5206\u3002</p><h3 id="_3-2-\u5730\u7406\u4F4D\u7F6E\u53CA\u4EA4\u901A" tabindex="-1"><a class="header-anchor" href="#_3-2-\u5730\u7406\u4F4D\u7F6E\u53CA\u4EA4\u901A" aria-hidden="true">#</a> 3.2 \u5730\u7406\u4F4D\u7F6E\u53CA\u4EA4\u901A</h3><p>\u8FD9\u4E00\u90E8\u5206\u5E94\u5F53\u4EE5\u6821\u533A\u540D\u79F0\u4F4D\u4E8E <code>[\u901A\u8BAF\u5730\u5740](POI \u94FE\u63A5)</code> \u7684\u5F62\u5F0F\u5F00\u5934\u3002\u540C\u6837\u5730\uFF0C\u4E0A\u6D77\u6D77\u6D0B\u5927\u5B66\u9875\u9762\u53EF\u4EE5\u4F5C\u4E3A\u53C2\u8003\u3002\u5728\u6B64\u5E94\u8BE5\u9610\u660E\u54EA\u4E9B\u5B66\u751F\u5C06\u4F1A\u8FDB\u5165\u54EA\u4E2A\u6821\u533A\uFF0C\u662F\u6309\u5E74\u7EA7\u5206\u914D\u6821\u533A\u8FD8\u662F\u6309\u4E13\u4E1A\u5206\u914D\u6821\u533A\u7B49\u7B49\u3002\u5982\u679C\u67D0\u4E2A\u6821\u533A\u79BB\u5E02\u533A\u5F88\u8FDC\uFF0C\u6216\u8005\u4EA4\u901A\u4E0D\u4FBF\uFF0C\u5E94\u5F53\u6307\u51FA\u3002</p><p>\u5982\u679C\u5B66\u6821\u8BBE\u6709\u591A\u4E2A\u6821\u533A\uFF0C\u5728\u63A5\u4E0B\u6765\u7684\u5185\u5BB9\u4E2D\uFF0C\u8BF7\u6CE8\u610F\u4F60\u6240\u9648\u8FF0\u7684\u662F\u5426\u662F\u6240\u6709\u6821\u533A\u7684\u666E\u904D\u60C5\u51B5\u3002\u5982\u679C\u4F60\u53EA\u4E86\u89E3\u81EA\u5DF1\u6240\u5728\u7684\u6821\u533A\u7684\u60C5\u51B5\uFF0C\u800C\u5BF9\u5176\u4ED6\u6821\u533A\u5E76\u4E0D\u4E86\u89E3\uFF0C\u5E94\u5F53\u6307\u51FA\u3002</p><h3 id="_3-3-\u519B\u8BAD\u4E0E\u4F53\u80B2" tabindex="-1"><a class="header-anchor" href="#_3-3-\u519B\u8BAD\u4E0E\u4F53\u80B2" aria-hidden="true">#</a> 3.3 \u519B\u8BAD\u4E0E\u4F53\u80B2</h3><p>\u519B\u8BAD\u90E8\u5206\u91CD\u70B9\u5173\u6CE8\uFF1A\u519B\u8BAD\u662F\u5426\u5F3A\u5236\u8981\u6C42\u526A\u77ED\u5934\u53D1\uFF0C\u519B\u8BAD\u7684\u65F6\u957F\u3001\u5F3A\u5EA6\uFF0C\u519B\u8BAD\u80FD\u5426\u514D\u8BAD\u6216\u534A\u8BAD\u7B49\u7B49\uFF1B\u4F53\u80B2\u90E8\u5206\u91CD\u70B9\u5173\u6CE8\uFF1A\u4F53\u80B2\u8BFE\u662F\u5426\u7537\u5973\u5206\u5F00\uFF0C\u4F53\u80B2\u8BFE\u5F3A\u5EA6\u3001\u8003\u6838\u96BE\u5EA6\uFF0C\u662F\u5426\u53EF\u4EE5\u7533\u8BF7\u964D\u4F4E\u6807\u51C6\u7B49\u7B49\u3002</p><h3 id="_3-4-\u5BBF\u820D\u60C5\u51B5\u4E0E\u57FA\u7840\u8BBE\u65BD" tabindex="-1"><a class="header-anchor" href="#_3-4-\u5BBF\u820D\u60C5\u51B5\u4E0E\u57FA\u7840\u8BBE\u65BD" aria-hidden="true">#</a> 3.4 \u5BBF\u820D\u60C5\u51B5\u4E0E\u57FA\u7840\u8BBE\u65BD</h3><p>\u5BBF\u820D\u60C5\u51B5\u91CD\u70B9\u5173\u6CE8\uFF1A\u5BDD\u5BA4\u73AF\u5883\u3001\u662F\u5426\u6709\u72EC\u7ACB\u536B\u6D74\uFF0C\u662F\u5426\u53EF\u4EE5\u7533\u8BF7\u8C03\u6362\u5BDD\u5BA4\u4E43\u81F3\u4E8E\u5355\u4EBA\u5C45\u4F4F\uFF0C\u662F\u5426\u53EF\u4EE5\u6821\u5916\u79DF\u623F\u5C45\u4F4F\u7B49\u7B49\uFF1B\u57FA\u7840\u8BBE\u65BD\u90E8\u5206\u91CD\u70B9\u5173\u6CE8\uFF1A\u5B66\u6821\u5404\u5904\u662F\u5426\u6709\u7B2C\u4E09\u536B\u751F\u95F4\u3002</p><h3 id="_3-5-\u533B\u7597\u8D44\u6E90" tabindex="-1"><a class="header-anchor" href="#_3-5-\u533B\u7597\u8D44\u6E90" aria-hidden="true">#</a> 3.5 \u533B\u7597\u8D44\u6E90</h3><p>\u533B\u7597\u8D44\u6E90\u90E8\u5206\u5305\u62EC\uFF1A\u6821\u533B\u9662\u80FD\u5426\u5F00\u5177\u6FC0\u7D20\u68C0\u67E5\uFF0C\u80FD\u5426\u516C\u8D39\u6216\u533B\u4FDD\u8D2D\u4E70 HRT\u836F\u7269\uFF1B\u5982\u679C\u4E0D\u80FD\uFF0C\u6821\u5916\u7684\u533B\u9662\u80FD\u5426\u8FDB\u884C\u68C0\u67E5\u4E0E\u5F00\u5177\u836F\u7269\uFF0C\u80FD\u5426\u56DE\u6821\u62A5\u9500\uFF0C\u524D\u5F80\u6821\u5916\u533B\u9662\u4E0E\u62A5\u9500\u662F\u5426\u65B9\u4FBF\u7B49\u7B49\u3002</p><h3 id="_3-6-lgbt-\u53CB\u5584\u6897\u6982" tabindex="-1"><a class="header-anchor" href="#_3-6-lgbt-\u53CB\u5584\u6897\u6982" aria-hidden="true">#</a> 3.6 LGBT \u53CB\u5584\u6897\u6982</h3><p>\u8FD9\u91CC\u53EF\u4EE5\u5199\u4E00\u4E9B\u4E3B\u89C2\u611F\u53D7\uFF0C\u5982\u5B66\u6821\u3001\u6559\u5E08\u3001\u540C\u5B66\u662F\u5426\u5BF9 LGBT \u53CB\u5584\u7B49\uFF0C\u5BA2\u89C2\u5185\u5BB9\u5305\u62EC\u5B66\u6821\u662F\u5426\u6709 LGBT \u4E13\u95E8\u7684\u793E\u56E2\u7EC4\u7EC7\uFF0C\u662F\u5426\u63D0\u4F9B LGBT \u7684\u79D1\u666E\u7B49\u3002\u5927\u90E8\u5206\u5B66\u6821\u90FD\u6709\u63D0\u4F9B\u5FC3\u7406\u54A8\u8BE2\u670D\u52A1\uFF0C\u5982\u6709\u5FC3\u7406\u54A8\u8BE2\u7684\u7ECF\u5386\uFF0C\u53EF\u4EE5\u8BB2\u8FF0\u5FC3\u7406\u54A8\u8BE2\u662F\u5426\u8DE8\u6027\u522B\u53CB\u5584\u3001\u9884\u7EA6\u662F\u5426\u5BB9\u6613\u7B49\u7B49\u3002</p><p>\u5982\u679C\u613F\u610F\uFF0C\u5728\u6B64\u8FD8\u53EF\u4EE5\u5217\u5199\u4E00\u4E0B\u5B66\u6821\u5185\u7684\u8DE8\u6027\u522B\u5206\u5E03\u60C5\u51B5\u3002</p><h3 id="_3-7-\u9662\u7CFB\u63A2\u8DEF" tabindex="-1"><a class="header-anchor" href="#_3-7-\u9662\u7CFB\u63A2\u8DEF" aria-hidden="true">#</a> 3.7 \u9662\u7CFB\u63A2\u8DEF</h3><p>\u7531\u4E8E\u4E0D\u540C\u9662\u7CFB\u4E4B\u95F4\u53EF\u80FD\u5DEE\u5F02\u8F83\u5927\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5728\u8FD9\u91CC\u5199\u4E0B\u4F60\u6240\u5728\u7684\u9662\u7CFB\u6C1B\u56F4\u5982\u4F55\uFF0C\u9662\u7CFB\u8001\u5E08\u3001\u540C\u5B66\u662F\u5426\u8DE8\u6027\u522B\u53CB\u5584\u7B49\u7B49\u3002</p><h3 id="_3-8-\u5176\u4ED6\u4FE1\u606F" tabindex="-1"><a class="header-anchor" href="#_3-8-\u5176\u4ED6\u4FE1\u606F" aria-hidden="true">#</a> 3.8 \u5176\u4ED6\u4FE1\u606F</h3><p>\u5982\u679C\u4F60\u8BA4\u4E3A\u8FD8\u6709\u5176\u4ED6\u9700\u8981\u653E\u5728 Wiki \u4E0A\u7684\u5185\u5BB9\uFF0C\u53EF\u4EE5\u586B\u5199\u5728\u8FD9\u91CC\u3002</p><h2 id="_4-\u7F72\u540D\u4E0E\u8054\u7CFB\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_4-\u7F72\u540D\u4E0E\u8054\u7CFB\u65B9\u5F0F" aria-hidden="true">#</a> 4 \u7F72\u540D\u4E0E\u8054\u7CFB\u65B9\u5F0F</h2><p>\u5982\u679C\u4F60\u613F\u610F\uFF0C\u53EF\u4EE5\u8FDB\u884C\u7F72\u540D\uFF0C\u4EE5 <code>{{&lt; sign-off-by &quot; \u4F60\u7684\u540D\u79F0&quot; &gt;}}</code> \u7684\u5F62\u5F0F\u3002\u4F60\u8FD8\u53EF\u4EE5\u7559\u4E0B\u4F60\u7684\u8054\u7CFB\u65B9\u5F0F\uFF0C\u5982\u90AE\u7BB1\u7B49\uFF0C\u65B9\u4FBF\u5B66\u6821\u7684\u5176\u4ED6\u8DE8\u6027\u522B\u6216\u662F\u6709\u610F\u5411\u62A5\u8003\u4F60\u7684\u5B66\u6821\u7684\u8DE8\u6027\u522B\u8054\u7CFB\u4F60\u3002\u4F60\u4E5F\u53EF\u4EE5\u4EE5\u79C1\u804A\u7684\u5F62\u5F0F\u548C\u4ED6\u4EEC\u4EA4\u6D41\u5B66\u6821\u7684\u66F4\u591A\u60C5\u51B5\u3002</p><p>\u6700\u540E\uFF0C\u611F\u8C22\u4F60\u5BF9\u8DE8\u6027\u522B\u793E\u7FA4\u7684\u8D21\u732E\u3002</p>',30),t=[i];function n(c,p){return e(),h("div",null,t)}var o=a(d,[["render",n],["__file","campus.html.vue"]]);export{o as default};
